// Minimal numeric.js stub for matrix operations
// Source: https://github.com/sloisel/numeric
window.numeric = {
  random: function(shape) {
    return Array.from({ length: shape[0] }, () =>
      Array.from({ length: shape[1] }, () => Math.random())
    );
  },
  dot: function(matrix, scalar) {
    return matrix.map(row => row.map(val => val * scalar));
  },
  eig: function(matrix) {
    // Dummy eigen decomposition for demo purposes
    const size = matrix.length;
    return {
      E: { x: Array.from({ length: size }, (_, i) =>
        Array.from({ length: size }, (_, j) => (i === j ? 1 : 0))
      )}
    };
  }
};
